const lightningDiv=document.querySelector("#lightning");
const animate=document.querySelector("animate");
const centralize=document.querySelector('input[name="centralize"]');
const amount=document.querySelector('input[name="amount"]');

const lightningsSVGs=[];
let lightningIntv;

fetch("./lightning.json")
.then((data)=>{
    return data.json()
}).then((data)=>{
   // console.log("json",data.LightningSVGs)
   Object.values(data.LightningSVGs).forEach(svg=>lightningsSVGs.push(svg));
   // document.querySelector("body").insertAdjacentHTML("afterbegin",lightningsSVGs[0])
   // document.querySelector("body").insertAdjacentHTML("afterbegin",lightningsSVGs[2])
   // document.querySelector("body").insertAdjacentHTML("afterbegin",lightningsSVGs[5])
   // document.querySelector("body").insertAdjacentHTML("afterbegin",lightningsSVGs[4])
   // document.querySelector("body").insertAdjacentHTML("afterbegin",lightningsSVGs[3])
   // document.querySelector("body").insertAdjacentHTML("afterbegin",lightningsSVGs[1])
   
});

// console.log("Array",lightningsSVGs)


// lightningDiv.insertAdjacentHTML("afterbegin",L1)
// lightningDiv.insertAdjacentHTML("afterbegin",L2)
// lightningDiv.insertAdjacentHTML("afterbegin",L3)

centralize.oninput=(e)=>{
   
   clearInterval(lightningIntv);
   makeLightning(100+900*(1-amount.value), e.target.value);

};

amount.oninput=(e)=>{
   // console.log("amount", e.target.value,100+900*(1-e.target.value))

   clearInterval(lightningIntv);
   makeLightning(100+900*(1-e.target.value), +centralize.value);
};


const makeLightning=(sec, center)=>{

   // console.log("centr",center)

   lightningIntv=setInterval(()=>{
   
      const randNum=Math.ceil(Math.random()*6);
      
      const rand=Math.random();
   
      const LL=
      randNum==1?lightningsSVGs[0]:
      randNum==2?lightningsSVGs[1]:
      randNum==3?lightningsSVGs[2]:
      randNum==4?lightningsSVGs[3]:
      randNum==5?lightningsSVGs[4]:
      lightningsSVGs[5];
      
      
      lightningDiv.insertAdjacentHTML("afterbegin",LL);
      // console.log(lightningDiv.firstChild)
      lightningDiv.firstChild.onanimationend=(e)=>e.target.remove();
      
      // lightningDiv.firstChild.style.left=`${rand*100}%`;
      
      // console.log("%%",+center + (50-center)*2*rand)
      // lightningDiv.firstChild.style.left=`50%`;

      lightningDiv.firstChild.style.left=`${+center/2 + (100-center)*1*rand}%`;

   
      lightningDiv.firstChild.style.top=`${(-1500+innerHeight+50)*rand}px`;
      lightningDiv.firstChild.style.animationDelay=`${rand*3}s`;
      
      console.log("Children",lightningDiv.childNodes.length)
      
   
   },sec);
};



makeLightning(100+900*(1-amount.value), +centralize.value);


